CREATE TABLE SETTINGS(id INTEGER PRIMARY KEY AUTOINCREMENT); CREATE TABLE PROJECT(id INTEGER PRIMARY KEY AUTOINCREMENT, title TEXT, description TEXT); CREATE TABLE CATEGORY(id INTEGER PRIMARY KEY AUTOINCREMENT, title TEXT, description TEXT); CREATE TABLE TODO(id INTEGER PRIMARY KEY AUTOINCREMENT, done INTEGER, archived INTEGER, level INTEGER, todo TEXT, description TEXT, create_time TEXT, last_update_time TEXT, project_id INTEGER NULL ON DELETE SET NULL, category_id INTEGER NULL ON DELETE SET NULL, FOREIGN KEY(project_id) REFERENCES PROJECT(id), FOREIGN KEY(category_id) REFERENCES CATEGORY(id));
